import{_ as e,o as l,c as a,a as i}from"./app.20213b8a.js";const p=JSON.parse(`{"title":"语法","description":"","frontmatter":{},"headers":[{"level":2,"title":"时态","slug":"时态","link":"#时态","children":[{"level":3,"title":"现在进行时","slug":"现在进行时","link":"#现在进行时","children":[]},{"level":3,"title":"一般将来时","slug":"一般将来时","link":"#一般将来时","children":[]},{"level":3,"title":"一般现在时","slug":"一般现在时","link":"#一般现在时","children":[]},{"level":3,"title":"一般过去时","slug":"一般过去时","link":"#一般过去时","children":[]},{"level":3,"title":"现在完成时","slug":"现在完成时","link":"#现在完成时","children":[]},{"level":3,"title":"过去完成时","slug":"过去完成时","link":"#过去完成时","children":[]}]},{"level":2,"title":"直接引语 间接引语","slug":"直接引语-间接引语","link":"#直接引语-间接引语","children":[]},{"level":2,"title":"adj 比较级 & 最高级","slug":"adj-比较级-最高级","link":"#adj-比较级-最高级","children":[]},{"level":2,"title":"little few","slug":"little-few","link":"#little-few","children":[]},{"level":2,"title":"否定疑问句","slug":"否定疑问句","link":"#否定疑问句","children":[]},{"level":2,"title":"not any/a/an = no+n = none","slug":"not-any-a-an-no-n-none","link":"#not-any-a-an-no-n-none","children":[]},{"level":2,"title":"固定结构","slug":"固定结构","link":"#固定结构","children":[{"level":3,"title":"There be + ...","slug":"there-be","link":"#there-be","children":[]},{"level":3,"title":"I have get a lot of money.=I have a lot of money.","slug":"i-have-get-a-lot-of-money-i-have-a-lot-of-money","link":"#i-have-get-a-lot-of-money-i-have-a-lot-of-money","children":[]},{"level":3,"title":"adj+enough","slug":"adj-enough","link":"#adj-enough","children":[]},{"level":3,"title":"want to do sth. = wanna do sth.","slug":"want-to-do-sth-wanna-do-sth","link":"#want-to-do-sth-wanna-do-sth","children":[]},{"level":3,"title":"so neither","slug":"so-neither","link":"#so-neither","children":[]}]},{"level":2,"title":"情态动词","slug":"情态动词","link":"#情态动词","children":[{"level":3,"title":"can","slug":"can","link":"#can","children":[]},{"level":3,"title":"must","slug":"must","link":"#must","children":[]},{"level":3,"title":"will","slug":"will","link":"#will","children":[]}]},{"level":2,"title":"feel / look / sound / smell / taste 感官动词用形容词修饰","slug":"feel-look-sound-smell-taste-感官动词用形容词修饰","link":"#feel-look-sound-smell-taste-感官动词用形容词修饰","children":[]},{"level":2,"title":"短语","slug":"短语","link":"#短语","children":[{"level":3,"title":"had better not do","slug":"had-better-not-do","link":"#had-better-not-do","children":[]},{"level":3,"title":"as ... as","slug":"as-as","link":"#as-as","children":[]},{"level":3,"title":"have to VS must","slug":"have-to-vs-must","link":"#have-to-vs-must","children":[]}]},{"level":2,"title":"表推测","slug":"表推测","link":"#表推测","children":[{"level":3,"title":"must 表示确信度高的肯定推测","slug":"must-表示确信度高的肯定推测","link":"#must-表示确信度高的肯定推测","children":[]},{"level":3,"title":"can't 表示确信度高的否定推测","slug":"can-t-表示确信度高的否定推测","link":"#can-t-表示确信度高的否定推测","children":[]},{"level":3,"title":"may/might 表示确信度低的肯定推测：可能是","slug":"may-might-表示确信度低的肯定推测-可能是","link":"#may-might-表示确信度低的肯定推测-可能是","children":[]}]},{"level":2,"title":"从句","slug":"从句","link":"#从句","children":[{"level":3,"title":"宾语从句","slug":"宾语从句","link":"#宾语从句","children":[]},{"level":3,"title":"时间（状语）从句","slug":"时间-状语-从句","link":"#时间-状语-从句","children":[]},{"level":3,"title":"定语从句","slug":"定语从句","link":"#定语从句","children":[]}]}],"relativePath":"english/grammer.md","lastUpdated":1692266010000}`),t={name:"english/grammer.md"},h=i('<h1 id="语法" tabindex="-1">语法 <a class="header-anchor" href="#语法" aria-hidden="true">#</a></h1><h2 id="时态" tabindex="-1">时态 <a class="header-anchor" href="#时态" aria-hidden="true">#</a></h2><h3 id="现在进行时" tabindex="-1">现在进行时 <a class="header-anchor" href="#现在进行时" aria-hidden="true">#</a></h3><p>主语+am/is/are+not+doing(v.ing)</p><ul><li>I am running.</li><li>She is not reading book.</li><li>Are they sitting on the sofa?</li><li>What are you doing?</li></ul><h3 id="一般将来时" tabindex="-1">一般将来时 <a class="header-anchor" href="#一般将来时" aria-hidden="true">#</a></h3><p>书面：主语+am/is/are+going to do(v) 口语：主语+am/is/are+gonna do</p><ul><li>I am going to wash my clothes.</li><li>Are you going to take a bus?</li><li>What she is going to do with the vase?</li></ul><h3 id="一般现在时" tabindex="-1">一般现在时 <a class="header-anchor" href="#一般现在时" aria-hidden="true">#</a></h3><p>主语+do（肯定句省略了 do）+动词原形+其他</p><p>含义：客观事实/存在状态/习惯动作（一般现在时并非表现在）</p><ul><li>I like tea</li><li>I （do） like tea (do 表强调，助动词，肯定句常年省略)</li><li>I don&#39;t like tea (否定句不能省略)</li><li>Do you like tea?</li><li>What do you like?</li><li>what don&#39;t you like?</li></ul><p>主语是第三人称单数 She likes tea. She does&#39;t like tea</p><ul><li><ol><li>s x ch sh o 结尾的 V. 加 es watch-watches go-goes wash-washes do-does</li></ol></li><li><ol start="2"><li>辅音字母加 y 结尾，改 y 为 i 加 es cry-cries fry-fries</li></ol></li><li><ol start="3"><li>元音字母加 y 结尾，直接 加 s buy-buys</li></ol></li></ul><h3 id="一般过去时" tabindex="-1">一般过去时 <a class="header-anchor" href="#一般过去时" aria-hidden="true">#</a></h3><p>主语+ was/were/did(动词的过去式)+ 其他</p><ul><li>过去的状态 am/is -&gt; was are -&gt; were</li><li>过去的动作 do/dose -&gt;did</li><li>I was a teacher</li><li>She packed her bag yesterday.</li><li>Did she pack her bag yesterday?</li></ul><h3 id="现在完成时" tabindex="-1">现在完成时 <a class="header-anchor" href="#现在完成时" aria-hidden="true">#</a></h3><p>主+have done(动词过去分词)+其他</p><p>用法：</p><ol><li>言之过去，意在当下</li><li>一个动作 done 或者状态 been 从过去持续或者重复到现在</li></ol><ul><li><p>She has had lunch</p></li><li><p>I have had some food,so I dont&#39;t need eat.</p></li><li><p>I&#39;ve already had lunch.</p></li><li><p>She has packed her bag.</p></li><li><p>Has she packed her bag?</p></li><li><p>I have lived in beijing for 10 years.</p></li><li><p>I have been Beijing for 10 years.</p></li><li><p>have you been Beijing for 10 years?</p></li></ul><p><strong>have been/gone to 区别</strong></p><p>have been to 去了但是已经回来，翻译成 去过 have gone to 去了但是还没回来，翻译成 去了</p><ul><li>I have been to bank.</li><li>She has gone to Beijing.</li></ul><h3 id="过去完成时" tabindex="-1">过去完成时 <a class="header-anchor" href="#过去完成时" aria-hidden="true">#</a></h3><p>had done had not done 用法： 强调过去某一动作发生在另一动作或状态之前.(过去的过去) 经常与 after before 连用</p><ol><li>after A , B = B after A</li></ol><ul><li>After I had sold the old car,I bought a new one.</li><li>I bought a new car after I had sold the old one.</li></ul><ol start="2"><li>Before B,A=A before B.</li></ol><ul><li>Before they ate the dinner,they had washed their hands.</li><li>They had washed their hands before they ate the dinner.</li></ul><h2 id="直接引语-间接引语" tabindex="-1">直接引语 间接引语 <a class="header-anchor" href="#直接引语-间接引语" aria-hidden="true">#</a></h2><p>间接引语 是个 宾语从句</p><ul><li>He says,&quot;I can do it&quot;.</li><li>He says (that) he can do it.</li><li>She says that she is tired.</li></ul><p>时态变化规则：</p><ol><li>一般现在时变一般过去时</li><li>现在进行时变过去进行时</li><li>现在完成时 或者 一般过去时 都变成 过去完成时</li><li>一般将来时 变成 过去将来时</li></ol><ul><li>She said that she was tired.</li><li>He said that he had lost his key.</li><li>She said that she was going to take a bath.</li></ul><h2 id="adj-比较级-最高级" tabindex="-1">adj 比较级 &amp; 最高级 <a class="header-anchor" href="#adj-比较级-最高级" aria-hidden="true">#</a></h2><p>构成</p><ol><li>单音节形容词 + er/est</li></ol><ul><li>small -&gt; smaller -&gt; smallest</li><li>new -&gt; newer -&gt; newest</li><li>old -&gt; older -&gt; oldest</li></ul><ol start="2"><li>单词以 e 结尾 + r/st</li></ol><ul><li>large -&gt; larger -&gt; largest</li><li>nice -&gt; nicer -&gt; nicest</li></ul><ol start="3"><li>辅音字母 + y 结尾的，改 y 为 i ，加 er,est</li></ol><ul><li>pretty -&gt; prettier -&gt; prettiest</li><li>easy -&gt; easier -&gt; easiest</li><li>heavy -&gt; heavier -&gt; heaviest</li></ul><ol start="4"><li>辅+ 元+ 辅 ，双写末尾辅音字母，加 er,est</li></ol><ul><li>big -&gt; bigger -&gt; biggest</li><li>hot -&gt; hotter -&gt;hottest</li><li>fat -&gt; fatter -&gt;fattest</li></ul><ol start="5"><li>多音节形容词 前加 more / most 前加 less / least 表较少</li></ol><ul><li>the most beautiful</li><li>the least expensive 不是那么贵</li></ul><ol start="6"><li>不规则举例</li></ol><ul><li>many/much more most</li><li>little less least</li><li>good better best</li><li>bad worse worst</li></ul><p>主语 + be + <a href="http://adj.er" target="_blank" rel="noreferrer">adj.er</a> + than + 比较对象</p><ul><li>He is older than me</li></ul><p>主语 + be + the + adj.est + n + 范围</p><ul><li>My mom is the kindest person in the world.</li></ul><p>主语 + be + the + adj.est + n + I have ever met/seen/known</p><ul><li>This is the craziest thing I&#39;ve ever done</li></ul><h2 id="little-few" tabindex="-1">little few <a class="header-anchor" href="#little-few" aria-hidden="true">#</a></h2><p>a little + un 肯定含义 little + un 否定含义</p><p>a few + cn 肯定含义 few + cn 否定含义 I&#39;ve got a few friends,so I&#39;m not lonely. I&#39;ve got few friends.I&#39;m sad and lonely.</p><h2 id="否定疑问句" tabindex="-1">否定疑问句 <a class="header-anchor" href="#否定疑问句" aria-hidden="true">#</a></h2><p>Are&#39;t you lucky? Don&#39;t you like tea?</p><h2 id="not-any-a-an-no-n-none" tabindex="-1">not any/a/an = no+n = none <a class="header-anchor" href="#not-any-a-an-no-n-none" aria-hidden="true">#</a></h2><ul><li>I have not got any time=I have got no time.</li><li>I have&#39;t got any money= I have no money.</li><li>There aren&#39;t any books = The are no books.</li><li>I haven&#39;t got any beer.= I have got no beer.= I have got none.</li></ul><h2 id="固定结构" tabindex="-1">固定结构 <a class="header-anchor" href="#固定结构" aria-hidden="true">#</a></h2><h3 id="there-be" tabindex="-1">There be + ... <a class="header-anchor" href="#there-be" aria-hidden="true">#</a></h3><ul><li>There is an apple on the table.</li><li>Are there any apples on the table?</li><li>There is some food in the box.</li><li>Is there any water here?</li></ul><h3 id="i-have-get-a-lot-of-money-i-have-a-lot-of-money" tabindex="-1">I have get a lot of money.=I have a lot of money. <a class="header-anchor" href="#i-have-get-a-lot-of-money-i-have-a-lot-of-money" aria-hidden="true">#</a></h3><h3 id="adj-enough" tabindex="-1">adj+enough <a class="header-anchor" href="#adj-enough" aria-hidden="true">#</a></h3><ul><li>This is too big.</li><li>This is big enough.</li></ul><h3 id="want-to-do-sth-wanna-do-sth" tabindex="-1">want to do sth. = wanna do sth. <a class="header-anchor" href="#want-to-do-sth-wanna-do-sth" aria-hidden="true">#</a></h3><h3 id="so-neither" tabindex="-1">so neither <a class="header-anchor" href="#so-neither" aria-hidden="true">#</a></h3><p>肯定：so + 助动词/情态动词/be + 主语 否定：neither + 助动词/情态动词/be + 主语 (这里原句哪个词变一般疑问句提前，那个词就放中间)</p><ul><li>I have a happy family. So do I.</li><li>I don&#39;t want to work. neither do I.</li></ul><h2 id="情态动词" tabindex="-1">情态动词 <a class="header-anchor" href="#情态动词" aria-hidden="true">#</a></h2><h3 id="can" tabindex="-1">can <a class="header-anchor" href="#can" aria-hidden="true">#</a></h3><p>主语 + can + not do sth.</p><ul><li>I can do my homework.</li><li>I can‘t help you.</li><li>Can you help me?</li><li>What can I do for you?</li></ul><h3 id="must" tabindex="-1">must <a class="header-anchor" href="#must" aria-hidden="true">#</a></h3><p>主语+ must + do sth 必需做什么</p><p>主语+ must‘t + do sth 禁止做什么（轻易不用）</p><ul><li>I must eat some food.</li><li>Must I do the housework?</li><li>What must she do?</li></ul><h3 id="will" tabindex="-1">will <a class="header-anchor" href="#will" aria-hidden="true">#</a></h3><p>主语+ will + do sth 将要做什么</p><h2 id="feel-look-sound-smell-taste-感官动词用形容词修饰" tabindex="-1">feel / look / sound / smell / taste 感官动词用形容词修饰 <a class="header-anchor" href="#feel-look-sound-smell-taste-感官动词用形容词修饰" aria-hidden="true">#</a></h2><ul><li>She feels great</li><li>look He looks ill</li><li>sound It dose‘t sound good.</li><li>smell Do i smell bad?</li><li>taste It tasets terrible.</li></ul><h2 id="短语" tabindex="-1">短语 <a class="header-anchor" href="#短语" aria-hidden="true">#</a></h2><h3 id="had-better-not-do" tabindex="-1">had better not do <a class="header-anchor" href="#had-better-not-do" aria-hidden="true">#</a></h3><p>最好怎么样</p><ul><li>You had better sleep.</li><li>She&#39;d better see a doctor.</li><li>He had better not eat any food.</li></ul><h3 id="as-as" tabindex="-1">as ... as <a class="header-anchor" href="#as-as" aria-hidden="true">#</a></h3><ul><li>The blue car is not as clean as the blue red car. 蓝车不如红车干净（ 蓝车 不干净 跟红车比）</li></ul><h3 id="have-to-vs-must" tabindex="-1">have to VS must <a class="header-anchor" href="#have-to-vs-must" aria-hidden="true">#</a></h3><p>have to 客观情况 must 主观的必须 必须/不得不 一般现在时</p><ul><li>I have to leave now.</li><li>I don&#39;t have to leave now.</li><li>Do you have to leave now.</li><li>She has to work from 9 to 5.</li></ul><p>一般将来时</p><ul><li>I will have to leave tomorrow.</li></ul><p>一般过去时</p><ul><li>I had to work for my family when I was 16.</li><li>I didn&#39;t have to work for my family.</li></ul><h2 id="表推测" tabindex="-1">表推测 <a class="header-anchor" href="#表推测" aria-hidden="true">#</a></h2><h3 id="must-表示确信度高的肯定推测" tabindex="-1">must 表示确信度高的肯定推测 <a class="header-anchor" href="#must-表示确信度高的肯定推测" aria-hidden="true">#</a></h3><p>对现在的推测：一定是 must be adj must be doing</p><p>对过去的推测：当时一定是 must have been must have done must have been doing.</p><ul><li>You must be Leo.</li><li>They must be drinking tea.</li><li>He must have been tired.</li></ul><h3 id="can-t-表示确信度高的否定推测" tabindex="-1">can&#39;t 表示确信度高的否定推测 <a class="header-anchor" href="#can-t-表示确信度高的否定推测" aria-hidden="true">#</a></h3><p>对现在的否定推测：一定不是 can&#39;t be adj can&#39;t be doing</p><ul><li>You can&#39;t be Leo.</li><li>They can&#39;t be drinking tea.</li></ul><p>对过去的否定推测：一定不是 can&#39;t have been can&#39;t have done can&#39;t have been doing.</p><ul><li>You can&#39;t have been on the bus.</li></ul><h3 id="may-might-表示确信度低的肯定推测-可能是" tabindex="-1">may/might 表示确信度低的肯定推测：可能是 <a class="header-anchor" href="#may-might-表示确信度低的肯定推测-可能是" aria-hidden="true">#</a></h3><p>现在的推测 may/might be adj/n may/might do may/might doing 对过去的推测 may/might have been adj/n. may/might have done may/might have been doing</p><ul><li>She may be from China.</li><li>She may have been a teacher.</li></ul><h2 id="从句" tabindex="-1">从句 <a class="header-anchor" href="#从句" aria-hidden="true">#</a></h2><h3 id="宾语从句" tabindex="-1">宾语从句 <a class="header-anchor" href="#宾语从句" aria-hidden="true">#</a></h3><p>主语+动词+（that）+句子 I think that you are right.</p><h3 id="时间-状语-从句" tabindex="-1">时间（状语）从句 <a class="header-anchor" href="#时间-状语-从句" aria-hidden="true">#</a></h3><p>句子作时间</p><ul><li>He was cooking a meal at 10 o&#39;clock.</li><li>He was cooking a meal (when he was listening to music).</li></ul><h4 id="when-while-的区别" tabindex="-1">when/while 的区别 <a class="header-anchor" href="#when-while-的区别" aria-hidden="true">#</a></h4><p>when + was doing/did while + was doing</p><h3 id="定语从句" tabindex="-1">定语从句 <a class="header-anchor" href="#定语从句" aria-hidden="true">#</a></h3><p>作用：确定具体是哪一个， 定语从句：一个句子作定语。 修饰人用 who/that 修饰物用 which/that</p><ul><li>The bread on the table.</li><li>The guy next to me.</li></ul>',123),n=[h];function o(r,d,s,u,c,g){return l(),a("div",null,n)}const b=e(t,[["render",o]]);export{p as __pageData,b as default};
